import './App.css';

import React, { Component } from 'react'
import Navbar from './components/Navbar';
import News from './components/News';
import {
  BrowserRouter as Router,
  Routes,
  Route,
} from "react-router-dom";
import LoadingBar from 'react-top-loading-bar';


export default class App extends Component {
  pageSize =5;
  apiKey = process.env.REACT_APP_NEWS_API
  state = {
    progress:0
  }

  setProgress = (progress) => {
    this.setState({progress : progress})
  }
  render() {
    return (
      <div>
        <Router>
        <Navbar/>
        <LoadingBar 
        height={3}
        color='#f11946'
        progress={this.state.progress}
        />
        <Routes>
          <Route path="/" exact element={<News setProgress={this.setProgress} apiKey={this.apiKey}  key="general" pageSize={this.pageSize}  country='in' category='general'/>}/>
          <Route path="/general" exact element={<News setProgress={this.setProgress} apiKey={this.apiKey}  key="general" pageSize={this.pageSize}  country='in' category='general'/>}/>
          <Route path="/business" exact element={<News setProgress={this.setProgress} apiKey={this.apiKey}  key="business" pageSize={this.pageSize}  country='in' category='business'/>}/>
          <Route path="/entertainment" exact element={<News setProgress={this.setProgress} apiKey={this.apiKey}  key="entertainment" pageSize={this.pageSize}  country='in' category='entertainment'/>}/>
          <Route path="/health" exact element={<News setProgress={this.setProgress} apiKey={this.apiKey}  key="health" pageSize={this.pageSize}  country='in' category='health'/>}/>
          <Route path="/science" exact element={<News setProgress={this.setProgress} apiKey={this.apiKey}  key="science" pageSize={this.pageSize}  country='in' category='science'/>}/>
          <Route path="/sports" exact element={<News setProgress={this.setProgress} apiKey={this.apiKey}  key="sports" pageSize={this.pageSize}  country='in' category='sports'/>}/>


        </Routes>

        </Router>
      </div>
    )
  }
}
